package com.example.activitytest;import android.app.Activity;import android.os.Bundle;import android.os.PersistableBundle;import android.support.v7.app.AppCompatActivity;import android.util.Log;/** * Created by shaomiao on 2016-12-29. */public class BaseActivity extends AppCompatActivity {    private static final String TAG = "BaseActivity";    @Override    public void onCreate(Bundle savedInstanceState, PersistableBundle persistentState) {        super.onCreate(savedInstanceState, persistentState);        Log.d(TAG, getClass().getSimpleName());        ActivityCollector.addActivity(this);    }    @Override    protected void onDestroy() {        super.onDestroy();        ActivityCollector.remoteActivity(this);    }}